import React, { useState, useEffect } from "react";
import classes from "./MultiPlayer.module.css";
import Player from "./Player";

const useMultiAudio = (urls, playIsOn) => {
  const [seconds, setSeconds] = useState(0);
  const [isAnyButtonOn, setIsAnyButtonOn] = useState(false);
  const [loop, setLoop] = useState(false);

  const [sources] = useState(
    urls.map((url) => {
      return {
        url,
        audio: new Audio(url),
      };
    })
  );

  const [players, setPlayers] = useState([
    {
      instroment: "SilentStar",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "Electric Guitar",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "PAS3GROOVE1",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "MazePolitics",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "GrooveB",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "FUD_120",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "Bass",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "stutter",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
    {
      instroment: "future_funk",
      check: false,
      playing: false,
      startTime: 0,
      duration: 0,
    },
  ]);

  const toggle = (targetIndex) => () => {
    const newPlayers = [...players];
    if (newPlayers[targetIndex].playing === false) {
      newPlayers[targetIndex].playing = true;
    } else {
      newPlayers[targetIndex].playing = false;
    }

    setPlayers(newPlayers);
  };

  useEffect(() => {
    if (playIsOn) {
      sources.forEach((source, i) => {
        if (players[i].playing) {
          source.audio.load();
          source.audio.play();
        } else {
          source.audio.pause();
        }
      });
      //setSeconds(8);

      setTimeout(() => {
        setLoop(!loop);
      }, 8000);
    } else {
      sources.forEach((source, i) => {
        source.audio.pause();
      });
    }
  }, [loop]);

  useEffect(() => {
    const interval = setInterval(() => {
      console.log("This will run every second!");
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  //   useEffect(() => {
  //     if (playIsOn) {
  //       sources.forEach((source, i) => {
  //         players[i].playing ? source.audio.play() : source.audio.pause();
  //       });
  //       setSeconds(8);
  //     } else {
  //       sources.forEach((source, i) => {
  //         source.audio.pause();
  //       });
  //     }
  //   });

  return [players, toggle];
};

const MultiPlayer = (props) => {
  const [players, toggle] = useMultiAudio(props.urls, props.playIsOn);

  return (
    <div>
      {players.map((player, i) => (
        <Player key={i} player={player} toggle={toggle(i)} />
      ))}
    </div>
  );
};

export default MultiPlayer;
