import classes from "./RecordButton.module.css";

const RecordButton = (props) => {
  const [recordIsOn, setRecordIsOn] = useState(false);

  const showPlaySessionHandler = () => {
    setRecordIsOn(true);
  };

  const hidePlaySessionHandler = () => {
    setRecordIsOn(false);
  };

  return (
    <div>
      <button className={classes.pad} onClick={props.onClick}>
        <p>{props.name}</p>
        <span>{props.state ? "On" : "Off"}</span>
      </button>
    </div>
  );
};

export default RecordButton;
